  /* RTL support improvements */
  [dir="rtl"] {
    text-align: right;
  }
  
  [dir="rtl"] .rtl-flip {
    transform: scaleX(-1);
  }
  
  /* Fix for RTL dropdowns */
  [dir="rtl"] [data-radix-select-content] {
    direction: rtl;
    text-align: right;
  }
  
  [dir="rtl"] [role="combobox"] {
    text-align: right;
    direction: rtl;
  }
  
  [dir="rtl"] [role="option"] {
    text-align: right;
    padding-left: 2rem;
    padding-right: 0.75rem;
    direction: rtl;
  }
  
  [dir="rtl"] [role="option"] [data-radix-select-item-indicator] {
    left: 0.75rem;
    right: auto;
  }

  /* Fix for RTL select trigger content */
  [dir="rtl"] [data-radix-select-trigger] {
    text-align: right;
    direction: rtl;
  }

  /* Fix for RTL select value */
  [dir="rtl"] [data-radix-select-value] {
    text-align: right;
    direction: rtl;
  }

  /* Arabic text and long text handling */
  [dir="rtl"] {
    word-break: break-word;
    overflow-wrap: break-word;
    -webkit-hyphens: auto;
            hyphens: auto;
  }

  /* Improve text wrapping for Arabic labels */
  [dir="rtl"] label {
    word-break: break-word;
    line-height: 1.4;
  }

  /* Better handling of long text in select elements for Arabic */
  [dir="rtl"] [role="combobox"] {
    min-height: 48px;
